  --shadow-md: 0px 2px 0px 0px hsl(210 17% 5% / 0.15), 0px 2px 4px -1px hsl(210 17% 5% / 0.25);
  --shadow-lg: 0px 2px 0px 0px hsl(210 17% 5% / 0.15), 0px 4px 6px -1px hsl(210 17% 5% / 0.30);
  --shadow-xl: 0px 2px 0px 0px hsl(210 17% 5% / 0.15), 0px 8px 10px -1px hsl(210 17% 5% / 0.35);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 17% 5% / 0.15);

