import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { 
  Ship, 
  TrendingUp, 
  TrendingDown, 
  AlertCircle, 
  CheckCircle2, 
  Fuel, 
  Euro, 
  Target, 
  Globe, 
  Calculator,
  Plus,
  X,
  Edit,
  BarChart3,
  Clock,
  Settings
} from "lucide-react";
import { FUELEU_MARITIME, EU_ETS, IMO_NET_ZERO, UK_ETS, calculateFuelEUPenalty, calculateEUETSCost, calculateIMOGap } from "@/lib/regulatoryConstants";

interface DynamicTilesProps {
  frameworks: {
    fuelEUMaritime: boolean;
    euETS: boolean;
    imoNetZero: boolean;
    ukETS: boolean;
  };
  fleetData: {
    totalVessels: number;
    compliant: number;
    warning: number;
    nonCompliant: number;
    averageIntensity: number;
    totalCredits: number;
  };
  vesselData: {
    grossTonnage: number;
    fuelConsumption: number;
    ghgIntensity: number;
    voyageType: 'intra-eu' | 'extra-eu';
  };
  customTiles: string[];
  onAddCustomTile: (tileType: string) => void;
  onRemoveCustomTile: (tileId: string) => void;
}

const DynamicDashboardTiles = ({ 
  frameworks, 
  fleetData, 
  vesselData, 
  customTiles, 
  onAddCustomTile, 
  onRemoveCustomTile 
}: DynamicTilesProps) => {
  const [showTileManager, setShowTileManager] = useState(false);

  // Calculate priority scores for each framework based on compliance status
  const getFrameworkPriority = () => {
    const priorities = [];

    if (frameworks.fuelEUMaritime) {
      const target = FUELEU_MARITIME.targets[2025].intensity;
      const gap = Math.max(0, vesselData.ghgIntensity - target);
      const urgency = gap > 0 ? 3 : fleetData.nonCompliant > 0 ? 2 : 1;
      priorities.push({ framework: 'fuelEUMaritime', urgency, gap, type: 'compliance' });
    }

    if (frameworks.euETS) {
      const cost = calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2025);
      const urgency = cost > 200000 ? 3 : cost > 100000 ? 2 : 1;
      priorities.push({ framework: 'euETS', urgency, cost, type: 'cost' });
    }

    if (frameworks.imoNetZero) {
      const assessment = calculateIMOGap(vesselData.ghgIntensity, 2025);
      const urgency = !assessment.isCompliant ? 3 : 1;
      priorities.push({ framework: 'imoNetZero', urgency, gap: assessment.gap, type: 'trajectory' });
    }

    if (frameworks.ukETS) {
      const ukCoverage = vesselData.voyageType === 'intra-eu' ? 0.25 : 0.15;
      const cost = vesselData.fuelConsumption * UK_ETS.emissionFactor * ukCoverage * UK_ETS.allowancePrice * UK_ETS.currencyConversion;
      const urgency = cost > 100000 ? 2 : 1;
      priorities.push({ framework: 'ukETS', urgency, cost, type: 'cost' });
    }

    return priorities.sort((a, b) => b.urgency - a.urgency);
  };

  // Render default overview tiles when no frameworks are selected
  const renderDefaultOverviewTiles = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      <Card className="hover-elevate border border-blue-500">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Ship className="h-5 w-5 text-blue-600" />
            <CardTitle className="text-base">Fleet Overview</CardTitle>
          </div>
          <Badge variant="secondary">Current Status</Badge>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="flex justify-between text-sm">
              <span>Total Vessels:</span>
              <span className="font-medium">{fleetData.totalVessels}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Compliant:</span>
              <span className="font-medium text-green-600">{fleetData.compliant}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Warning:</span>
              <span className="font-medium text-yellow-600">{fleetData.warning}</span>
            </div>
            <div className="flex justify-between text-sm">
              <span>Non-Compliant:</span>
              <span className="font-medium text-red-600">{fleetData.nonCompliant}</span>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card className="hover-elevate border border-green-500">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <TrendingUp className="h-5 w-5 text-green-600" />
            <CardTitle className="text-base">Performance Metrics</CardTitle>
          </div>
          <Badge variant="secondary">Fleet Average</Badge>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">{fleetData.averageIntensity.toFixed(1)}</div>
              <div className="text-xs text-muted-foreground">gCO2e/MJ average intensity</div>
            </div>
            <div className="flex justify-between text-sm">
              <span>Total Credits:</span>
              <span className="font-medium">{fleetData.totalCredits.toLocaleString()}</span>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card className="hover-elevate border border-purple-500">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Settings className="h-5 w-5 text-purple-600" />
            <CardTitle className="text-base">Getting Started</CardTitle>
          </div>
          <Badge variant="secondary">Quick Setup</Badge>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <p className="text-sm text-muted-foreground">
              Select regulatory frameworks above to see compliance analysis and recommendations
            </p>
            <div className="text-xs text-muted-foreground mt-2">
              Available frameworks: FuelEU Maritime, EU ETS, IMO Net Zero, UK ETS
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );

  // Render priority tiles based on active frameworks
  const renderPriorityTiles = () => {
    const priorities = getFrameworkPriority();
    const activeTiles: JSX.Element[] = [];

    // Add critical compliance tiles first
    priorities.forEach(priority => {
      if (priority.urgency >= 3) {
        switch (priority.framework) {
          case 'fuelEUMaritime':
            activeTiles.push(
              <Card key="fueleu-critical" className="hover-elevate border border-red-500 bg-red-50/50 dark:bg-red-950/20">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <AlertCircle className="h-5 w-5 text-red-600" />
                      <CardTitle className="text-base text-red-700 dark:text-red-400">FuelEU Maritime - Critical</CardTitle>
                    </div>
                    <Badge variant="destructive">Action Required</Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="text-center">
                      <div className="text-2xl font-bold text-red-600">{priority.gap?.toFixed(1)} gCO2e/MJ</div>
                      <div className="text-xs text-muted-foreground">Above target</div>
                    </div>
                    <div className="space-y-2">
                      <div className="text-sm font-medium text-red-700 dark:text-red-400">Recommended Actions:</div>
                      <ul className="text-xs text-muted-foreground space-y-1">
                        <li>â€¢ Consider fuel switching to MGO/LNG</li>
                        <li>â€¢ Explore credit pooling opportunities</li>
                        <li>â€¢ Review voyage optimization</li>
                      </ul>
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
            break;
          case 'euETS':
            activeTiles.push(
              <Card key="euets-critical" className="hover-elevate border border-orange-500 bg-orange-50/50 dark:bg-orange-950/20">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Euro className="h-5 w-5 text-orange-600" />
                      <CardTitle className="text-base text-orange-700 dark:text-orange-400">EU ETS - High Cost</CardTitle>
                    </div>
                    <Badge variant="secondary">â‚¬{(priority.cost || 0).toLocaleString()}</Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="text-sm font-medium text-orange-700 dark:text-orange-400">Cost Reduction Options:</div>
                    <ul className="text-xs text-muted-foreground space-y-1">
                      <li>â€¢ Purchase EUAs in advance</li>
                      <li>â€¢ Optimize route planning</li>
                      <li>â€¢ Consider alternative fuels</li>
                      <li>â€¢ Review MOHA balance regularly</li>
                    </ul>
                  </div>
                </CardContent>
              </Card>
            );
            break;
          case 'imoNetZero':
            activeTiles.push(
              <Card key="imo-critical" className="hover-elevate border border-yellow-500 bg-yellow-50/50 dark:bg-yellow-950/20">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Target className="h-5 w-5 text-yellow-600" />
                      <CardTitle className="text-base text-yellow-700 dark:text-yellow-400">IMO Net Zero - Off Track</CardTitle>
                    </div>
                    <Badge variant="secondary">2030 Target</Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    <div className="text-sm font-medium text-yellow-700 dark:text-yellow-400">Trajectory Improvement:</div>
                    <ul className="text-xs text-muted-foreground space-y-1">
                      <li>â€¢ Develop decarbonization plan</li>
                      <li>â€¢ Assess green fuel availability</li>
                      <li>â€¢ Consider efficiency measures</li>
                      <li>â€¢ Monitor technology developments</li>
                    </ul>
                  </div>
                </CardContent>
              </Card>
            );
            break;
        }
      }
    });

    // Add standard priority tiles
    priorities.forEach(priority => {
      if (priority.urgency < 3) {
        switch (priority.framework) {
          case 'fuelEUMaritime':
            activeTiles.push(
              <Card key="fueleu-normal" className="hover-elevate border border-blue-500">
                <CardHeader className="pb-3">
                  <div className="flex items-center space-x-2">
                    <CheckCircle2 className="h-5 w-5 text-green-600" />
                    <CardTitle className="text-base">FuelEU Maritime - Compliant</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">{vesselData.ghgIntensity.toFixed(1)}</div>
                    <div className="text-xs text-muted-foreground">gCO2e/MJ (Below target)</div>
                    <div className="mt-2 text-xs text-green-600">Maintain current performance</div>
                  </div>
                </CardContent>
              </Card>
            );
            break;
          case 'euETS':
            activeTiles.push(
              <Card key="euets-normal" className="hover-elevate border border-purple-500">
                <CardHeader className="pb-3">
                  <div className="flex items-center space-x-2">
                    <Euro className="h-5 w-5 text-purple-600" />
                    <CardTitle className="text-base">EU ETS - Manageable Cost</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-purple-600">â‚¬{(priority.cost || 0).toLocaleString()}</div>
                    <div className="text-xs text-muted-foreground">Annual EUA cost</div>
                    <div className="mt-2 text-xs text-purple-600">Monitor EUA prices</div>
                  </div>
                </CardContent>
              </Card>
            );
            break;
          case 'imoNetZero':
            activeTiles.push(
              <Card key="imo-normal" className="hover-elevate border border-green-500">
                <CardHeader className="pb-3">
                  <div className="flex items-center space-x-2">
                    <Target className="h-5 w-5 text-green-600" />
                    <CardTitle className="text-base">IMO Net Zero - On Track</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-600">
                      {((FUELEU_MARITIME.baseline - vesselData.ghgIntensity) / FUELEU_MARITIME.baseline * 100).toFixed(1)}%
                    </div>
                    <div className="text-xs text-muted-foreground">Reduction achieved</div>
                    <div className="mt-2 text-xs text-green-600">Continue progress</div>
                  </div>
                </CardContent>
              </Card>
            );
            break;
          case 'ukETS':
            activeTiles.push(
              <Card key="ukets-normal" className="hover-elevate border border-rose-500">
                <CardHeader className="pb-3">
                  <div className="flex items-center space-x-2">
                    <Globe className="h-5 w-5 text-rose-600" />
                    <CardTitle className="text-base">UK ETS - Moderate Impact</CardTitle>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="text-center">
                    <div className="text-2xl font-bold text-rose-600">â‚¬{(priority.cost || 0).toLocaleString()}</div>
                    <div className="text-xs text-muted-foreground">Annual UK cost</div>
                    <div className="mt-2 text-xs text-rose-600">Monitor UK operations</div>
                  </div>
                </CardContent>
              </Card>
            );
            break;
        }
      }
    });

    return activeTiles;
  };

  // Render custom tiles
  const renderCustomTiles = () => {
    return customTiles.map((tileType, index) => (
      <Card key={`custom-${index}`} className="hover-elevate border border-gray-400">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5 text-gray-600" />
              <CardTitle className="text-base">{tileType}</CardTitle>
            </div>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={() => onRemoveCustomTile(`custom-${index}`)}
              data-testid={`remove-tile-${index}`}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center">
            <div className="text-sm text-muted-foreground">Custom tile content for {tileType}</div>
          </div>
        </CardContent>
      </Card>
    ));
  };

  const availableCustomTiles = [
    "Fleet Performance Summary",
    "Cost Optimization Tracker",
    "Technology Readiness Assessment",
    "Market Price Monitor",
    "Regulatory Updates",
    "Credit Trading Opportunities"
  ];

  const hasActiveFrameworks = Object.values(frameworks).some(Boolean);
  const priorityTiles = hasActiveFrameworks ? renderPriorityTiles() : [];
  const customTileComponents = renderCustomTiles();

  return (
    <div className="space-y-6">
      {/* Tile Management Controls */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-lg font-medium">Dashboard Tiles</h3>
          <p className="text-sm text-muted-foreground">
            {hasActiveFrameworks 
              ? `Showing priority tiles for ${Object.values(frameworks).filter(Boolean).length} active framework(s)`
              : "Enable frameworks above to see relevant compliance tiles"
            }
          </p>
        </div>
        <Button 
          variant="outline" 
          onClick={() => setShowTileManager(!showTileManager)}
          data-testid="manage-tiles-button"
        >
          <Plus className="h-4 w-4 mr-2" />
          Manage Tiles
        </Button>
      </div>

      {/* Tile Manager */}
      {showTileManager && (
        <Card className="border-dashed">
          <CardHeader>
            <CardTitle className="text-base">Add Custom Tiles</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
              {availableCustomTiles.map((tileType) => (
                <Button
                  key={tileType}
                  variant="outline"
                  size="sm"
                  onClick={() => onAddCustomTile(tileType)}
                  disabled={customTiles.includes(tileType)}
                  data-testid={`add-tile-${tileType.toLowerCase().replace(/\s+/g, '-')}`}
                >
                  <Plus className="h-3 w-3 mr-1" />
                  {tileType}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Main Tiles Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {hasActiveFrameworks ? (
          <>
            {priorityTiles}
            {customTileComponents}
          </>
        ) : (
          <>
            {renderDefaultOverviewTiles()}
            {customTileComponents}
          </>
        )}
      </div>
    </div>
  );
};

export default DynamicDashboardTiles;

client/src/components/EnhancedCompliance.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Ship, 
  TrendingUp, 
  TrendingDown, 
  AlertCircle, 
  CheckCircle2, 
  Fuel, 
  Euro, 
  Target, 
  Globe, 
  Calculator,
  Plus,
  X,
  Lightbulb,
  ArrowRight,
  Clock,
  DollarSign,
  BarChart3,
  Settings
} from "lucide-react";
import { FUELEU_MARITIME, EU_ETS, IMO_NET_ZERO, UK_ETS, calculateFuelEUPenalty, calculateEUETSCost, calculateIMOGap } from "@/lib/regulatoryConstants";
import FrameworkSpecificTiles from "./FrameworkSpecificTiles";
import FrameworkVisualGraphs from "./FrameworkVisualGraphs";

interface EnhancedComplianceProps {
  frameworks: {
    fuelEUMaritime: boolean;
    euETS: boolean;
    imoNetZero: boolean;
    ukETS: boolean;
  };
  vesselData: {
    grossTonnage: number;
    fuelConsumption: number;
    ghgIntensity: number;
    voyageType: 'intra-eu' | 'extra-eu';
  };
  customTiles: string[];
  onAddCustomTile: (tileType: string) => void;
  onRemoveCustomTile: (tileId: string) => void;
}

const EnhancedCompliancePage = ({ 
  frameworks, 
  vesselData, 
  customTiles, 
  onAddCustomTile, 
  onRemoveCustomTile 
}: EnhancedComplianceProps) => {
  const [showTileManager, setShowTileManager] = useState(false);
  const [activeView, setActiveView] = useState<'overview' | 'detailed'>('overview');

  // Calculate compliance status and recommendations for each framework
  const getComplianceAnalysis = () => {
    const analysis = {
      fuelEUMaritime: {
        status: 'unknown' as 'compliant' | 'warning' | 'critical' | 'unknown',
        gap: 0,
        cost: 0,
        recommendations: [] as string[],
        nextSteps: [] as string[],
        timeToDeadline: 0
      },
      euETS: {
        status: 'unknown' as 'compliant' | 'warning' | 'critical' | 'unknown',
        gap: 0,
        cost: 0,
        recommendations: [] as string[],
        nextSteps: [] as string[],
        timeToDeadline: 0
      },
      imoNetZero: {
        status: 'unknown' as 'compliant' | 'warning' | 'critical' | 'unknown',
        gap: 0,
        cost: 0,
        recommendations: [] as string[],
        nextSteps: [] as string[],
        timeToDeadline: 0
      },
      ukETS: {
        status: 'unknown' as 'compliant' | 'warning' | 'critical' | 'unknown',
        gap: 0,
        cost: 0,
        recommendations: [] as string[],
        nextSteps: [] as string[],
        timeToDeadline: 0
      }
    };

    if (frameworks.fuelEUMaritime) {
      const target = FUELEU_MARITIME.targets[2025].intensity;
      const gap = Math.max(0, vesselData.ghgIntensity - target);
      const energyUsed = vesselData.fuelConsumption * 1000 * 40.2;
      const penalty = calculateFuelEUPenalty(vesselData.ghgIntensity, target, energyUsed);

      analysis.fuelEUMaritime = {
        status: gap === 0 ? 'compliant' : gap > 5 ? 'critical' : 'warning',
        gap,
        cost: penalty,
        recommendations: gap > 0 ? [
          "Switch to Marine Gas Oil (MGO) for improved efficiency",
          "Investigate bio-fuel blending opportunities",
          "Optimize vessel speed and routing",
          "Consider participation in pooling mechanism"
        ] : [
          "Maintain current operational efficiency",
          "Monitor fuel quality and consumption",
          "Consider banking excess credits"
        ],
        nextSteps: gap > 0 ? [
          "Review fuel procurement strategy",
          "Assess alternative fuel infrastructure",
          "Calculate pooling/banking opportunities",
          "Prepare emissions monitoring plan"
        ] : [
          "Continue compliance monitoring",
          "Evaluate credit trading opportunities"
        ],
        timeToDeadline: 128 // days to next deadline
      };
    }

    if (frameworks.euETS) {
      const cost = calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2025);

      analysis.euETS = {
        status: cost > 300000 ? 'critical' : cost > 150000 ? 'warning' : 'compliant',
        gap: 0,
        cost,
        recommendations: cost > 200000 ? [
          "Purchase EUAs in advance to hedge price risk",
          "Optimize route planning to reduce EU coverage",
          "Consider fuel switching for EU voyages",
          "Monitor MOHA balance closely"
        ] : [
          "Monitor EUA price trends",
          "Maintain adequate MOHA balance",
          "Plan for full phase-in by 2027"
        ],
        nextSteps: [
          "Set up THETIS-MRV monitoring",
          "Establish EUA procurement strategy",
          "Review voyage reporting procedures",
          "Calculate 2026 phase-in impact"
        ],
        timeToDeadline: 249 // days to surrender deadline
      };
    }

    if (frameworks.imoNetZero) {
      const assessment = calculateIMOGap(vesselData.ghgIntensity, 2025);

      analysis.imoNetZero = {
        status: assessment.isCompliant ? 'compliant' : 'warning',
        gap: assessment.gap,
        cost: assessment.gap * vesselData.fuelConsumption * 1000 * 40.2 * IMO_NET_ZERO.economicMeasures.estimatedLevy / 1000000,
        recommendations: !assessment.isCompliant ? [
          "Develop long-term decarbonization strategy",
          "Assess green ammonia/hydrogen feasibility",
          "Implement energy efficiency measures",
          "Consider shore power connections"
        ] : [
          "Continue trajectory monitoring",
          "Prepare for 2030 milestone review",
          "Evaluate emerging technologies"
        ],
        nextSteps: [
          "Create technology roadmap to 2030",
          "Assess port infrastructure availability",
          "Evaluate economic measures impact",
          "Monitor IMO regulatory developments"
        ],
        timeToDeadline: 1825 // days to 2030 target
      };
    }

    if (frameworks.ukETS) {
      const ukCoverage = vesselData.voyageType === 'intra-eu' ? 0.25 : 0.15;
      const cost = vesselData.fuelConsumption * UK_ETS.emissionFactor * ukCoverage * UK_ETS.allowancePrice * UK_ETS.currencyConversion;

      analysis.ukETS = {
        status: cost > 100000 ? 'warning' : 'compliant',
        gap: 0,
        cost,
        recommendations: cost > 50000 ? [
          "Monitor GBP/EUR exchange rates",
          "Optimize UK port call schedules",
          "Consider UK-specific fuel strategies",
          "Track UK ETS price developments"
        ] : [
          "Monitor UK regulatory updates",
          "Maintain UK operations efficiency"
        ],
        nextSteps: [
          "Review UK port operations",
          "Monitor UK ETS policy changes",
          "Assess post-Brexit implications",
          "Plan for potential coverage expansion"
        ],
        timeToDeadline: 365 // placeholder for UK deadlines
      };
    }

    return analysis;
  };

  const analysis = getComplianceAnalysis();

  // Render actionable recommendations
  const renderRecommendations = (frameworkKey: keyof typeof analysis) => {
    const frameworkAnalysis = analysis[frameworkKey];
    if (!frameworks[frameworkKey] || frameworkAnalysis.status === 'unknown') return null;

    const getStatusColor = (status: string) => {
      switch (status) {
        case 'compliant': return 'text-green-600';
        case 'warning': return 'text-yellow-600';
        case 'critical': return 'text-red-600';
        default: return 'text-gray-600';
      }
    };

    const getStatusIcon = (status: string) => {
      switch (status) {
        case 'compliant': return <CheckCircle2 className="h-5 w-5 text-green-600" />;
        case 'warning': return <AlertCircle className="h-5 w-5 text-yellow-600" />;
        case 'critical': return <AlertCircle className="h-5 w-5 text-red-600" />;
        default: return <Ship className="h-5 w-5 text-gray-600" />;
      }
    };

    const frameworkNames = {
      fuelEUMaritime: 'FuelEU Maritime',
      euETS: 'EU ETS',
      imoNetZero: 'IMO Net Zero',
      ukETS: 'UK ETS'
    };

    return (
      <Card key={frameworkKey} className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {getStatusIcon(frameworkAnalysis.status)}
              <CardTitle className="text-lg">{frameworkNames[frameworkKey]}</CardTitle>
            </div>
            <div className="text-right">
              <Badge variant={frameworkAnalysis.status === 'compliant' ? 'default' : 
                              frameworkAnalysis.status === 'warning' ? 'secondary' : 'destructive'}>
                {frameworkAnalysis.status.charAt(0).toUpperCase() + frameworkAnalysis.status.slice(1)}
              </Badge>
              <div className="text-sm text-muted-foreground mt-1">
                {frameworkAnalysis.timeToDeadline > 0 && 
                  `${frameworkAnalysis.timeToDeadline} days to next milestone`
                }
              </div>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {/* Status Summary */}
            <div className="grid grid-cols-2 gap-4">
              {frameworkAnalysis.gap > 0 && (
                <div className="text-center">
                  <div className={`text-xl font-bold ${getStatusColor(frameworkAnalysis.status)}`}>
                    {frameworkAnalysis.gap.toFixed(1)}
                  </div>
                  <div className="text-xs text-muted-foreground">Compliance Gap</div>
                </div>
              )}
              {frameworkAnalysis.cost > 0 && (
                <div className="text-center">
                  <div className={`text-xl font-bold ${getStatusColor(frameworkAnalysis.status)}`}>
                    â‚¬{frameworkAnalysis.cost.toLocaleString()}
                  </div>
                  <div className="text-xs text-muted-foreground">Annual Cost/Penalty</div>
                </div>
              )}
            </div>

            {/* Recommendations */}
            {frameworkAnalysis.recommendations.length > 0 && (
              <div>
                <div className="flex items-center space-x-2 mb-2">
                  <Lightbulb className="h-4 w-4 text-blue-600" />
                  <span className="text-sm font-medium">Recommendations</span>
                </div>
                <ul className="space-y-1">
                  {frameworkAnalysis.recommendations.map((rec, index) => (
                    <li key={index} className="text-sm text-muted-foreground flex items-start space-x-2">
                      <ArrowRight className="h-3 w-3 mt-1 text-blue-600 flex-shrink-0" />
                      <span>{rec}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}

            {/* Next Steps */}
            {frameworkAnalysis.nextSteps.length > 0 && (
              <div>
                <div className="flex items-center space-x-2 mb-2">
                  <Clock className="h-4 w-4 text-purple-600" />
                  <span className="text-sm font-medium">Next Steps</span>
                </div>
                <ul className="space-y-1">
                  {frameworkAnalysis.nextSteps.slice(0, 3).map((step, index) => (
                    <li key={index} className="text-sm text-muted-foreground flex items-start space-x-2">
                      <span className="flex-shrink-0 w-4 h-4 rounded-full bg-purple-100 text-purple-600 text-xs flex items-center justify-center mt-0.5">
                        {index + 1}
                      </span>
                      <span>{step}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    );
  };

  // Render custom compliance tiles
  const renderCustomComplianceTiles = () => {
    return customTiles.map((tileType, index) => (
      <Card key={`compliance-custom-${index}`} className="hover-elevate border border-gray-400">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5 text-gray-600" />
              <CardTitle className="text-base">{tileType}</CardTitle>
            </div>
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={() => onRemoveCustomTile(`compliance-custom-${index}`)}
              data-testid={`remove-compliance-tile-${index}`}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center">
            <div className="text-sm text-muted-foreground">Custom compliance analysis for {tileType}</div>
          </div>
        </CardContent>
      </Card>
    ));
  };

  const availableComplianceTiles = [
    "Regulatory Timeline Tracker",
    "Cost Impact Analysis",
    "Technology Assessment",
    "Port Compliance Status",
    "Credit Trading Monitor",
    "Operational Efficiency Metrics"
  ];

  const hasActiveFrameworks = Object.values(frameworks).some(Boolean);

  return (
    <div className="space-y-6">
      {/* Header with Actions */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Multi-Framework Compliance</h2>
          <p className="text-muted-foreground">
            {hasActiveFrameworks 
              ? `Analyzing compliance across ${Object.values(frameworks).filter(Boolean).length} active framework(s)`
              : "Enable frameworks on the Dashboard to view compliance analysis"
            }
          </p>
        </div>
        <div className="flex space-x-2">
          <Button 
            variant={activeView === 'overview' ? 'default' : 'outline'}
            onClick={() => setActiveView('overview')}
            data-testid="view-overview"
          >
            Overview
          </Button>
          <Button 
            variant={activeView === 'detailed' ? 'default' : 'outline'}
            onClick={() => setActiveView('detailed')}
            data-testid="view-detailed"
          >
            Detailed
          </Button>
          <Button 
            variant="outline" 
            onClick={() => setShowTileManager(!showTileManager)}
            data-testid="manage-compliance-tiles"
          >
            <Plus className="h-4 w-4 mr-2" />
            Manage Tiles
          </Button>
        </div>
      </div>

      {/* Tile Manager for Compliance */}
      {showTileManager && (
        <Card className="border-dashed">
          <CardHeader>
            <CardTitle className="text-base">Add Custom Compliance Tiles</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
              {availableComplianceTiles.map((tileType) => (
                <Button
                  key={tileType}
                  variant="outline"
                  size="sm"
                  onClick={() => onAddCustomTile(tileType)}
                  disabled={customTiles.includes(tileType)}
                  data-testid={`add-compliance-tile-${tileType.toLowerCase().replace(/\s+/g, '-')}`}
                >
                  <Plus className="h-3 w-3 mr-1" />
                  {tileType}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {hasActiveFrameworks ? (
        <Tabs defaultValue="analysis" className="w-full">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="analysis">Compliance Analysis</TabsTrigger>
            <TabsTrigger value="frameworks">Framework Details</TabsTrigger>
            <TabsTrigger value="insights">Visual Insights</TabsTrigger>
          </TabsList>

          <TabsContent value="analysis" className="space-y-4">
            {activeView === 'overview' ? (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {Object.keys(frameworks).map((key) => 
                  renderRecommendations(key as keyof typeof frameworks)
                ).filter(Boolean)}
                {renderCustomComplianceTiles()}
              </div>
            ) : (
              <div className="space-y-6">
                <FrameworkSpecificTiles 
                  frameworks={frameworks}
                  vesselData={vesselData}
                />
                {renderCustomComplianceTiles()}
              </div>
            )}
          </TabsContent>

          <TabsContent value="frameworks" className="space-y-4">
            <FrameworkSpecificTiles 
              frameworks={frameworks}
              vesselData={vesselData}
            />
          </TabsContent>

          <TabsContent value="insights" className="space-y-4">
            <FrameworkVisualGraphs 
              frameworks={frameworks}
              vesselData={vesselData}
            />
          </TabsContent>
        </Tabs>
      ) : (
        <Card className="text-center py-12">
          <CardContent>
            <Ship className="h-16 w-16 mx-auto mb-4 text-muted-foreground opacity-50" />
            <h3 className="text-lg font-medium mb-2">No Active Compliance Frameworks</h3>
            <p className="text-muted-foreground mb-6">
              Enable compliance frameworks on the Dashboard to see detailed compliance analysis, 
              recommendations, and actionable insights for your fleet.
            </p>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 max-w-4xl mx-auto">
              <div className="p-4 border rounded-lg hover-elevate">
                <Fuel className="h-8 w-8 text-blue-600 mb-2" />
                <h4 className="font-medium text-sm">FuelEU Maritime</h4>
                <p className="text-xs text-muted-foreground">GHG intensity tracking</p>
              </div>
              <div className="p-4 border rounded-lg hover-elevate">
                <Euro className="h-8 w-8 text-purple-600 mb-2" />
                <h4 className="font-medium text-sm">EU ETS</h4>
                <p className="text-xs text-muted-foreground">Emissions trading</p>
              </div>
              <div className="p-4 border rounded-lg hover-elevate">
                <Target className="h-8 w-8 text-green-600 mb-2" />
                <h4 className="font-medium text-sm">IMO Net Zero</h4>
                <p className="text-xs text-muted-foreground">Decarbonization targets</p>
              </div>
              <div className="p-4 border rounded-lg hover-elevate">
                <Globe className="h-8 w-8 text-rose-600 mb-2" />
                <h4 className="font-medium text-sm">UK ETS</h4>
                <p className="text-xs text-muted-foreground">UK emissions system</p>
              </div>
            </div>
            {renderCustomComplianceTiles()}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default EnhancedCompliancePage;

client/src/components/FrameworkVisualGra.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { LineChart, Line, AreaChart, Area, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from "recharts";
import { TrendingUp, TrendingDown, Target, Euro, Fuel, Globe } from "lucide-react";
import { FUELEU_MARITIME, EU_ETS, IMO_NET_ZERO, UK_ETS, calculateFuelEUPenalty, calculateEUETSCost, calculateIMOGap } from "@/lib/regulatoryConstants";

interface FrameworkGraphsProps {
  frameworks: {
    fuelEUMaritime: boolean;
    euETS: boolean;
    imoNetZero: boolean;
    ukETS: boolean;
  };
  vesselData: {
    grossTonnage: number;
    fuelConsumption: number;
    ghgIntensity: number;
    voyageType: 'intra-eu' | 'extra-eu';
  };
}

const FrameworkVisualGraphs = ({ frameworks, vesselData }: FrameworkGraphsProps) => {

  // FuelEU Maritime Trajectory Data using official targets
  const fuelEUTrajectoryData = [
    { year: 2025, target: FUELEU_MARITIME.targets[2025].intensity, current: vesselData.ghgIntensity, reduction: FUELEU_MARITIME.targets[2025].reduction },
    { year: 2026, target: FUELEU_MARITIME.targets[2026].intensity, current: vesselData.ghgIntensity * 0.95, reduction: FUELEU_MARITIME.targets[2026].reduction },
    { year: 2027, target: FUELEU_MARITIME.targets[2027].intensity, current: vesselData.ghgIntensity * 0.92, reduction: FUELEU_MARITIME.targets[2027].reduction },
    { year: 2028, target: FUELEU_MARITIME.targets[2028].intensity, current: vesselData.ghgIntensity * 0.89, reduction: FUELEU_MARITIME.targets[2028].reduction },
    { year: 2029, target: FUELEU_MARITIME.targets[2029].intensity, current: vesselData.ghgIntensity * 0.86, reduction: FUELEU_MARITIME.targets[2029].reduction },
    { year: 2030, target: FUELEU_MARITIME.targets[2030].intensity, current: vesselData.ghgIntensity * 0.80, reduction: FUELEU_MARITIME.targets[2030].reduction }
  ];

  // EU ETS Phase-in Schedule using official parameters
  const euETSPhaseData = [
    { year: 2024, coverage: EU_ETS.phaseIn[2024] * 100, cost: calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2024) },
    { year: 2025, coverage: EU_ETS.phaseIn[2025] * 100, cost: calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2025) },
    { year: 2026, coverage: EU_ETS.phaseIn[2026] * 100, cost: calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2026) },
    { year: 2027, coverage: EU_ETS.phaseIn[2027] * 100, cost: calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2027) },
    { year: 2028, coverage: 100, cost: vesselData.fuelConsumption * EU_ETS.emissionFactor * EU_ETS.voyageCoverage[vesselData.voyageType] * EU_ETS.allowancePrice * 1.1 },
    { year: 2029, coverage: 100, cost: vesselData.fuelConsumption * EU_ETS.emissionFactor * EU_ETS.voyageCoverage[vesselData.voyageType] * EU_ETS.allowancePrice * 1.2 },
    { year: 2030, coverage: 100, cost: vesselData.fuelConsumption * EU_ETS.emissionFactor * EU_ETS.voyageCoverage[vesselData.voyageType] * EU_ETS.allowancePrice * 1.3 }
  ];

  // IMO Net Zero Pathway using official 2023 strategy
  const imoNetZeroData = [
    { year: 2025, intensity: vesselData.ghgIntensity, target: calculateIMOGap(vesselData.ghgIntensity, 2025).targetIntensity, reduction: 4.3 },
    { year: 2027, intensity: vesselData.ghgIntensity * 0.92, target: calculateIMOGap(vesselData.ghgIntensity, 2027).targetIntensity, reduction: 8.5 },
    { year: 2030, intensity: vesselData.ghgIntensity * 0.80, target: IMO_NET_ZERO.targets[2030].intensity, reduction: IMO_NET_ZERO.targets[2030].reduction },
    { year: 2035, intensity: vesselData.ghgIntensity * 0.65, target: 59.25, reduction: 35 },
    { year: 2040, intensity: vesselData.ghgIntensity * 0.45, target: IMO_NET_ZERO.targets[2040].intensity, reduction: IMO_NET_ZERO.targets[2040].reduction },
    { year: 2045, intensity: vesselData.ghgIntensity * 0.25, target: 22.79, reduction: 75 },
    { year: 2050, intensity: vesselData.ghgIntensity * 0.10, target: IMO_NET_ZERO.targets[2050].intensity, reduction: IMO_NET_ZERO.targets[2050].reduction }
  ];

  // Cumulative Cost Breakdown by Framework using corrected calculations
  const energyUsed = vesselData.fuelConsumption * 1000 * 40.2; // Convert to MJ
  const fuelEUTarget = FUELEU_MARITIME.targets[2025].intensity;
  const imoAssessment = calculateIMOGap(vesselData.ghgIntensity, 2025);
  const ukCoverage = vesselData.voyageType === 'intra-eu' ? 0.25 : 0.15;

  const costBreakdownData = [
    {
      framework: "FuelEU Maritime",
      cost: calculateFuelEUPenalty(vesselData.ghgIntensity, fuelEUTarget, energyUsed),
      color: "#3B82F6"
    },
    {
      framework: "EU ETS", 
      cost: calculateEUETSCost(vesselData.fuelConsumption, vesselData.voyageType, 2025),
      color: "#8B5CF6"
    },
    {
      framework: "IMO Net Zero",
      cost: imoAssessment.gap * vesselData.fuelConsumption * 1000 * 40.2 * IMO_NET_ZERO.economicMeasures.estimatedLevy / 1000000,
      color: "#10B981"
    },
    {
      framework: "UK ETS",
      cost: vesselData.fuelConsumption * UK_ETS.emissionFactor * ukCoverage * UK_ETS.allowancePrice * UK_ETS.currencyConversion,
      color: "#F59E0B"
    }
  ].filter(item => {
    if (item.framework === "FuelEU Maritime") return frameworks.fuelEUMaritime;
    if (item.framework === "EU ETS") return frameworks.euETS;
    if (item.framework === "IMO Net Zero") return frameworks.imoNetZero;
    if (item.framework === "UK ETS") return frameworks.ukETS;
    return false;
  });

  // Monthly Compliance Trend (12 months)
  const monthlyTrendData = [
    { month: 'Jan', fuelEU: 89.1, euETS: 234000, imo: 87.5, ukETS: 45000 },
    { month: 'Feb', fuelEU: 88.8, euETS: 245000, imo: 87.2, ukETS: 47000 },
    { month: 'Mar', fuelEU: 89.4, euETS: 258000, imo: 87.8, ukETS: 49000 },
    { month: 'Apr', fuelEU: 88.9, euETS: 241000, imo: 87.1, ukETS: 46000 },
    { month: 'May', fuelEU: 89.2, euETS: 252000, imo: 87.4, ukETS: 48000 },
    { month: 'Jun', fuelEU: 88.7, euETS: 238000, imo: 86.9, ukETS: 45500 },
    { month: 'Jul', fuelEU: 89.0, euETS: 247000, imo: 87.2, ukETS: 47200 },
    { month: 'Aug', fuelEU: 88.6, euETS: 235000, imo: 86.8, ukETS: 44800 },
    { month: 'Sep', fuelEU: 89.3, euETS: 255000, imo: 87.5, ukETS: 48600 },
    { month: 'Oct', fuelEU: 88.9, euETS: 243000, imo: 87.1, ukETS: 46400 },
    { month: 'Nov', fuelEU: 89.1, euETS: 249000, imo: 87.3, ukETS: 47500 },
    { month: 'Dec', fuelEU: 88.8, euETS: 240000, imo: 87.0, ukETS: 45900 }
  ];

  const COLORS = ['#3B82F6', '#8B5CF6', '#10B981', '#F59E0B', '#EF4444', '#06B6D4'];

  const renderFuelEUGraphs = () => (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <TrendingDown className="h-4 w-4 text-blue-600" />
            <CardTitle className="text-base">GHG Intensity Trajectory (2025-2030)</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={fuelEUTrajectoryData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value, name) => [
                  `${Number(value).toFixed(1)} gCO2e/MJ`, 
                  name === 'target' ? 'FuelEU Target' : 'Projected Performance'
                ]}
              />
              <Line 
                type="monotone" 
                dataKey="target" 
                stroke="#3B82F6" 
                strokeWidth={2}
                strokeDasharray="5 5"
                dot={{ fill: '#3B82F6', strokeWidth: 2, r: 4 }}
              />
              <Line 
                type="monotone" 
                dataKey="current" 
                stroke="#10B981" 
                strokeWidth={2}
                dot={{ fill: '#10B981', strokeWidth: 2, r: 4 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>

      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Target className="h-4 w-4 text-green-600" />
            <CardTitle className="text-base">Reduction Targets Progress</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={fuelEUTrajectoryData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value) => [`${value}%`, 'Required Reduction']}
              />
              <Bar dataKey="reduction" fill="#10B981" radius={[2, 2, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );

  const renderEUETSGraphs = () => (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Euro className="h-4 w-4 text-purple-600" />
            <CardTitle className="text-base">Phase-in Cost Projection</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <AreaChart data={euETSPhaseData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value, name) => [
                  name === 'coverage' ? `${value}%` : `â‚¬${Number(value).toLocaleString()}`,
                  name === 'coverage' ? 'Coverage' : 'Annual Cost'
                ]}
              />
              <Area 
                type="monotone" 
                dataKey="cost" 
                stroke="#8B5CF6" 
                fill="#8B5CF6" 
                fillOpacity={0.3}
              />
            </AreaChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>

      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Globe className="h-4 w-4 text-cyan-600" />
            <CardTitle className="text-base">Coverage Phase-in Schedule</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={euETSPhaseData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis domain={[0, 100]} stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value) => [`${value}%`, 'Emissions Coverage']}
              />
              <Line 
                type="monotone" 
                dataKey="coverage" 
                stroke="#06B6D4" 
                strokeWidth={3}
                dot={{ fill: '#06B6D4', strokeWidth: 2, r: 5 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );

  const renderIMOGraphs = () => (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <Target className="h-4 w-4 text-emerald-600" />
            <CardTitle className="text-base">Net Zero Pathway (2025-2050)</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <AreaChart data={imoNetZeroData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value, name) => [
                  `${Number(value).toFixed(1)} gCO2e/MJ`,
                  name === 'target' ? 'IMO Target' : 'Projected Intensity'
                ]}
              />
              <Area 
                type="monotone" 
                dataKey="target" 
                stroke="#10B981" 
                fill="#10B981" 
                fillOpacity={0.2}
              />
              <Line 
                type="monotone" 
                dataKey="intensity" 
                stroke="#059669" 
                strokeWidth={2}
                dot={{ fill: '#059669', strokeWidth: 2, r: 4 }}
              />
            </AreaChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>

      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center space-x-2">
            <TrendingUp className="h-4 w-4 text-teal-600" />
            <CardTitle className="text-base">Reduction Milestones</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={imoNetZeroData.filter(d => [2030, 2040, 2050].includes(d.year))}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis dataKey="year" stroke="#666" fontSize={12} />
              <YAxis stroke="#666" fontSize={12} />
              <Tooltip 
                contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
                formatter={(value) => [`${value}%`, 'Reduction Target']}
              />
              <Bar dataKey="reduction" fill="#14B8A6" radius={[2, 2, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );

  const renderCumulativeCostGraph = () => (
    <Card className="hover-elevate border-2 border-primary/20">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Euro className="h-5 w-5 text-primary" />
            <CardTitle className="text-lg">Cumulative Compliance Cost Breakdown</CardTitle>
          </div>
          <Badge variant="secondary" data-testid="badge-total-cost">
            â‚¬{costBreakdownData.reduce((sum, item) => sum + item.cost, 0).toLocaleString('en-US', { maximumFractionDigits: 0 })} Annual
          </Badge>
        </div>
      </CardHeader>
      <CardContent>
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie
                data={costBreakdownData}
                cx="50%"
                cy="50%"
                outerRadius={80}
                fill="#8884d8"
                dataKey="cost"
                label={({ framework, cost }) => `${framework}: â‚¬${cost.toLocaleString('en-US', { maximumFractionDigits: 0 })}`}
              >
                {costBreakdownData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip 
                formatter={(value) => [`â‚¬${Number(value).toLocaleString()}`, 'Annual Cost']}
              />
            </PieChart>
          </ResponsiveContainer>

          <div className="space-y-4">
            {costBreakdownData.map((item, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-4 h-4 rounded-full" 
                    style={{ backgroundColor: item.color }}
                  />
                  <span className="font-medium">{item.framework}</span>
                </div>
                <div className="text-right">
                  <div className="font-bold" data-testid={`text-cost-${item.framework.toLowerCase().replace(/\s+/g, '-')}`}>â‚¬{item.cost.toLocaleString('en-US', { maximumFractionDigits: 0 })}</div>
                  <div className="text-xs text-muted-foreground">
                    {((item.cost / costBreakdownData.reduce((sum, i) => sum + i.cost, 0)) * 100).toFixed(1)}%
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </CardContent>
    </Card>
  );

  const renderMonthlyTrendGraph = () => (
    <Card className="hover-elevate">
      <CardHeader className="pb-3">
        <div className="flex items-center space-x-2">
          <TrendingUp className="h-5 w-5 text-blue-600" />
          <CardTitle className="text-lg">Monthly Compliance Trends</CardTitle>
        </div>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={monthlyTrendData}>
            <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
            <XAxis dataKey="month" stroke="#666" fontSize={12} />
            <YAxis stroke="#666" fontSize={12} />
            <Tooltip 
              contentStyle={{ backgroundColor: '#fff', border: '1px solid #ccc', borderRadius: '4px' }}
            />
            {frameworks.fuelEUMaritime && (
              <Line 
                type="monotone" 
                dataKey="fuelEU" 
                stroke="#3B82F6" 
                strokeWidth={2}
                name="FuelEU Maritime (gCO2e/MJ)"
                dot={{ fill: '#3B82F6', r: 3 }}
              />
            )}
            {frameworks.euETS && (
              <Line 
                type="monotone" 
                dataKey="euETS" 
                stroke="#8B5CF6" 
                strokeWidth={2}
                name="EU ETS Cost (â‚¬)"
                dot={{ fill: '#8B5CF6', r: 3 }}
              />
            )}
            {frameworks.imoNetZero && (
              <Line 
                type="monotone" 
                dataKey="imo" 
                stroke="#10B981" 
                strokeWidth={2}
                name="IMO Intensity (gCO2e/MJ)"
                dot={{ fill: '#10B981', r: 3 }}
              />
            )}
            {frameworks.ukETS && (
              <Line 
                type="monotone" 
                dataKey="ukETS" 
                stroke="#F59E0B" 
                strokeWidth={2}
                name="UK ETS Cost (â‚¬)"
                dot={{ fill: '#F59E0B', r: 3 }}
              />
            )}
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );

  if (!Object.values(frameworks).some(Boolean)) {
    return null;
  }

  return (
    <div className="space-y-6" data-testid="framework-visual-graphs">
      {/* Cumulative Cost Overview */}
      {costBreakdownData.length > 0 && renderCumulativeCostGraph()}

      {/* Framework-specific graphs */}
      {frameworks.fuelEUMaritime && (
        <div className="space-y-4">
          <div className="flex items-center space-x-2">
            <Fuel className="h-5 w-5 text-blue-600" />
            <h3 className="text-lg font-semibold text-blue-600">FuelEU Maritime Analytics</h3>
          </div>
          {renderFuelEUGraphs()}
        </div>
      )}

      {frameworks.euETS && (
        <div className="space-y-4">
          <div className="flex items-center space-x-2">
            <Euro className="h-5 w-5 text-purple-600" />
            <h3 className="text-lg font-semibold text-purple-600">EU ETS Analytics</h3>
          </div>
          {renderEUETSGraphs()}
        </div>
      )}

      {frameworks.imoNetZero && (
        <div className="space-y-4">
          <div className="flex items-center space-x-2">
            <Target className="h-5 w-5 text-emerald-600" />
            <h3 className="text-lg font-semibold text-emerald-600">IMO Net Zero Analytics</h3>
          </div>
          {renderIMOGraphs()}
        </div>
      )}

      {/* Monthly trends for all active frameworks */}
      {renderMonthlyTrendGraph()}
    </div>
  );
};

export default FrameworkVisualGraphs;

client/src/components/FuelConsumptionCha.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, BarChart, Bar } from "recharts";
import { TrendingUp, BarChart3 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useState } from "react";

interface FuelDataPoint {
  month: string;
  ghgIntensity: number;
  target: number;
  fuelConsumption: number;
  complianceCredits: number;
}

interface FuelConsumptionChartProps {
  data: FuelDataPoint[];
  title: string;
  vesselName?: string;
}

const FuelConsumptionChart = ({ data, title, vesselName }: FuelConsumptionChartProps) => {
  const [chartType, setChartType] = useState<"line" | "bar">("line");

  const toggleChartType = () => {
    setChartType(prev => prev === "line" ? "bar" : "line");
    console.log('Chart type toggled to:', chartType === "line" ? "bar" : "line");
  };

  const formatTooltip = (value: any, name: string) => {
    if (name === "ghgIntensity" || name === "target") {
      return [`${value} gCO2e/MJ`, name === "ghgIntensity" ? "GHG Intensity" : "Target"];
    }
    if (name === "fuelConsumption") {
      return [`${value} MT`, "Fuel Consumption"];
    }
    if (name === "complianceCredits") {
      return [`${value}`, "Compliance Credits"];
    }
    return [value, name];
  };

  return (
    <Card className="hover-elevate" data-testid="fuel-consumption-chart">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <TrendingUp className="h-5 w-5 text-muted-foreground" />
            <CardTitle>{title}</CardTitle>
            {vesselName && (
              <span className="text-sm text-muted-foreground">- {vesselName}</span>
            )}
          </div>
          <Button 
            variant="outline" 
            size="sm"
            onClick={toggleChartType}
            data-testid="button-toggle-chart"
          >
            <BarChart3 className="h-4 w-4 mr-1" />
            {chartType === "line" ? "Bar" : "Line"}
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        <div className="h-80 w-full">
          <ResponsiveContainer width="100%" height="100%">
            {chartType === "line" ? (
              <LineChart data={data}>
                <CartesianGrid strokeDasharray="3 3" className="opacity-30" />
                <XAxis 
                  dataKey="month" 
                  tick={{ fontSize: 12 }}
                  tickLine={{ stroke: 'hsl(var(--border))' }}
                />
                <YAxis 
                  tick={{ fontSize: 12 }}
                  tickLine={{ stroke: 'hsl(var(--border))' }}
                />
                <Tooltip 
                  formatter={formatTooltip}
                  contentStyle={{
                    backgroundColor: 'hsl(var(--card))',
                    border: '1px solid hsl(var(--border))',
                    borderRadius: '6px'
                  }}
                />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="ghgIntensity" 
                  stroke="hsl(var(--chart-1))" 
                  strokeWidth={2}
                  name="GHG Intensity"
                  dot={{ fill: 'hsl(var(--chart-1))', strokeWidth: 2 }}
                />
                <Line 
                  type="monotone" 
                  dataKey="target" 
                  stroke="hsl(var(--chart-2))" 
                  strokeWidth={2}
                  strokeDasharray="5 5"
                  name="Target"
                  dot={{ fill: 'hsl(var(--chart-2))', strokeWidth: 2 }}
                />
                <Line 
                  type="monotone" 
                  dataKey="complianceCredits" 
                  stroke="hsl(var(--chart-3))" 
                  strokeWidth={2}
                  name="Compliance Credits"
                  dot={{ fill: 'hsl(var(--chart-3))', strokeWidth: 2 }}
                />
              </LineChart>
            ) : (
              <BarChart data={data}>
                <CartesianGrid strokeDasharray="3 3" className="opacity-30" />
                <XAxis 
                  dataKey="month" 
                  tick={{ fontSize: 12 }}
                  tickLine={{ stroke: 'hsl(var(--border))' }}
                />
                <YAxis 
                  tick={{ fontSize: 12 }}
                  tickLine={{ stroke: 'hsl(var(--border))' }}
                />
                <Tooltip 
                  formatter={formatTooltip}
                  contentStyle={{
                    backgroundColor: 'hsl(var(--card))',
                    border: '1px solid hsl(var(--border))',
                    borderRadius: '6px'
                  }}
                />
                <Legend />
                <Bar 
                  dataKey="fuelConsumption" 
                  fill="hsl(var(--chart-1))" 
                  name="Fuel Consumption (MT)"
                />
                <Bar 
                  dataKey="ghgIntensity" 
                  fill="hsl(var(--chart-2))" 
                  name="GHG Intensity"
                />
              </BarChart>
            )}
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );
};

export default FuelConsumptionChart;

client/src/components/IntegratedFramewor.content:
import { useState, useEffect, useCallback } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { 
  Settings, 
  CheckCircle2, 
  AlertCircle, 
  RefreshCw, 
  BarChart3,
  Calculator,
  Target,
  TrendingUp
} from "lucide-react";

interface FrameworkState {
  fuelEUMaritime: boolean;
  euETS: boolean;
  imoNetZero: boolean;
  ukETS: boolean;
}

interface VesselData {
  grossTonnage: number;
  fuelConsumption: number;
  ghgIntensity: number;
  voyageType: 'intra-eu' | 'extra-eu';
}

interface IntegratedFrameworkManagerProps {
  frameworks: FrameworkState;
  vesselData: VesselData;
  onFrameworkChange: (frameworks: FrameworkState) => void;
  onDataRefresh: () => void;
  children: React.ReactNode;
}

const IntegratedFrameworkManager = ({
  frameworks,
  vesselData,
  onFrameworkChange,
  onDataRefresh,
  children
}: IntegratedFrameworkManagerProps) => {
  const [dataStatus, setDataStatus] = useState<'synced' | 'syncing' | 'error'>('synced');
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [integrationHealth, setIntegrationHealth] = useState<{
    calculatorSync: boolean;
    tilesSync: boolean;
    complianceSync: boolean;
    crossCalculation: boolean;
  }>({
    calculatorSync: true,
    tilesSync: true,
    complianceSync: true,
    crossCalculation: true
  });

  // Monitor framework changes and propagate to all components
  useEffect(() => {
    setDataStatus('syncing');

    // Simulate data synchronization across components
    const syncTimeout = setTimeout(() => {
      setDataStatus('synced');
      setLastUpdate(new Date());

      // Validate integration health
      validateIntegrationHealth();
    }, 500);

    return () => clearTimeout(syncTimeout);
  }, [frameworks, vesselData]);

  const validateIntegrationHealth = useCallback(() => {
    const activeFrameworkCount = Object.values(frameworks).filter(Boolean).length;

    setIntegrationHealth({
      calculatorSync: true, // Calculator properly receives framework data
      tilesSync: activeFrameworkCount > 0, // Tiles respond to framework changes
      complianceSync: true, // Compliance page shows framework-specific analysis
      crossCalculation: vesselData.ghgIntensity > 0 // Valid vessel data for calculations
    });
  }, [frameworks, vesselData]);

  const getFrameworkSummary = () => {
    const active = Object.values(frameworks).filter(Boolean).length;
    const total = Object.keys(frameworks).length;
    return { active, total, coverage: (active / total) * 100 };
  };

  const getDataQuality = () => {
    const quality = {
      vesselDataComplete: vesselData.grossTonnage > 0 && vesselData.fuelConsumption > 0,
      ghgIntensityValid: vesselData.ghgIntensity > 0 && vesselData.ghgIntensity < 200,
      voyageTypeSet: vesselData.voyageType !== undefined,
      calculationsReady: true
    };

    const score = Object.values(quality).filter(Boolean).length / Object.keys(quality).length * 100;
    return { quality, score };
  };

  const handleFrameworkToggle = (frameworkKey: keyof FrameworkState) => {
    const newFrameworks = {
      ...frameworks,
      [frameworkKey]: !frameworks[frameworkKey]
    };
    onFrameworkChange(newFrameworks);
  };

  const handleRefreshData = () => {
    setDataStatus('syncing');
    onDataRefresh();
  };

  const frameworkSummary = getFrameworkSummary();
  const dataQuality = getDataQuality();
  const allHealthy = Object.values(integrationHealth).every(Boolean);

  return (
    <div className="space-y-6">
      {/* Integration Status Header */}
      <Card className="hover-elevate">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Settings className="h-5 w-5 text-primary" />
              <CardTitle className="text-lg">Framework Integration Status</CardTitle>
            </div>
            <div className="flex items-center space-x-2">
              <Badge variant={dataStatus === 'synced' ? 'default' : 'secondary'}>
                {dataStatus === 'synced' ? 'Synchronized' : 'Syncing...'}
              </Badge>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={handleRefreshData}
                disabled={dataStatus === 'syncing'}
                data-testid="refresh-integration"
              >
                <RefreshCw className={`h-4 w-4 ${dataStatus === 'syncing' ? 'animate-spin' : ''}`} />
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Framework Coverage */}
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{frameworkSummary.active}/{frameworkSummary.total}</div>
              <div className="text-sm text-muted-foreground">Active Frameworks</div>
              <div className="text-xs text-muted-foreground mt-1">
                {frameworkSummary.coverage.toFixed(0)}% coverage
              </div>
            </div>

            {/* Data Quality */}
            <div className="text-center">
              <div className={`text-2xl font-bold ${dataQuality.score >= 80 ? 'text-green-600' : dataQuality.score >= 60 ? 'text-yellow-600' : 'text-red-600'}`}>
                {dataQuality.score.toFixed(0)}%
              </div>
              <div className="text-sm text-muted-foreground">Data Quality</div>
              <div className="text-xs text-muted-foreground mt-1">
                Vessel & calculation data
              </div>
            </div>

            {/* Integration Health */}
            <div className="text-center">
              <div className={`text-2xl font-bold ${allHealthy ? 'text-green-600' : 'text-yellow-600'}`}>
                {allHealthy ? <CheckCircle2 className="h-8 w-8 mx-auto" /> : <AlertCircle className="h-8 w-8 mx-auto" />}
              </div>
              <div className="text-sm text-muted-foreground">System Health</div>
              <div className="text-xs text-muted-foreground mt-1">
                Cross-component sync
              </div>
            </div>
          </div>

          {/* Integration Health Details */}
          {!allHealthy && (
            <Alert className="mt-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                <div className="space-y-1">
                  {!integrationHealth.tilesSync && <div>â€¢ Dashboard tiles not responding to framework changes</div>}
                  {!integrationHealth.calculatorSync && <div>â€¢ Calculator not receiving framework selections</div>}
                  {!integrationHealth.complianceSync && <div>â€¢ Compliance analysis not updated</div>}
                  {!integrationHealth.crossCalculation && <div>â€¢ Invalid vessel data for calculations</div>}
                </div>
              </AlertDescription>
            </Alert>
          )}

          {/* Quick Framework Toggles */}
          <div className="mt-4 pt-4 border-t">
            <div className="text-sm font-medium mb-2">Quick Framework Toggles:</div>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-2">
              <Button
                variant={frameworks.fuelEUMaritime ? "default" : "outline"}
                size="sm"
                onClick={() => handleFrameworkToggle('fuelEUMaritime')}
                data-testid="quick-toggle-fueleu"
              >
                FuelEU
              </Button>
              <Button
                variant={frameworks.euETS ? "default" : "outline"}
                size="sm"
                onClick={() => handleFrameworkToggle('euETS')}
                data-testid="quick-toggle-euets"
              >
                EU ETS
              </Button>
              <Button
                variant={frameworks.imoNetZero ? "default" : "outline"}
                size="sm"
                onClick={() => handleFrameworkToggle('imoNetZero')}
                data-testid="quick-toggle-imo"
              >
                IMO Net Zero
              </Button>
              <Button
                variant={frameworks.ukETS ? "default" : "outline"}
                size="sm"
                onClick={() => handleFrameworkToggle('ukETS')}
                data-testid="quick-toggle-ukgts"
              >
                UK ETS
              </Button>
            </div>
          </div>

          {/* Last Update Info */}
          <div className="mt-4 pt-4 border-t text-xs text-muted-foreground">
            Last synchronized: {lastUpdate.toLocaleTimeString()} | 
            Data status: {dataStatus} | 
            Health: {allHealthy ? 'All systems operational' : 'Some issues detected'}
          </div>
        </CardContent>
      </Card>

      {/* Child Components with Context */}
      <div className="space-y-6">
        {children}
      </div>

      {/* Cross-Component Data Summary */}
      {frameworkSummary.active > 1 && (
        <Card className="border-dashed">
          <CardHeader className="pb-3">
            <div className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5 text-blue-600" />
              <CardTitle className="text-base">Multi-Framework Analysis Summary</CardTitle>
            </div>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <div className="text-sm font-medium mb-2">Active Frameworks:</div>
                <div className="space-y-1">
                  {frameworks.fuelEUMaritime && (
                    <div className="flex items-center space-x-2">
                      <Badge variant="outline" className="text-xs">FuelEU Maritime</Badge>
                      <span className="text-xs text-muted-foreground">GHG intensity: {vesselData.ghgIntensity.toFixed(1)} gCO2e/MJ</span>
                    </div>
                  )}
                  {frameworks.euETS && (
                    <div className="flex items-center space-x-2">
                      <Badge variant="outline" className="text-xs">EU ETS</Badge>
                      <span className="text-xs text-muted-foreground">Coverage: {vesselData.voyageType === 'intra-eu' ? '100%' : '50%'}</span>
                    </div>
                  )}
                  {frameworks.imoNetZero && (
                    <div className="flex items-center space-x-2">
                      <Badge variant="outline" className="text-xs">IMO Net Zero</Badge>
                      <span className="text-xs text-muted-foreground">2030 trajectory monitoring</span>
                    </div>
                  )}
                  {frameworks.ukETS && (
                    <div className="flex items-center space-x-2">
                      <Badge variant="outline" className="text-xs">UK ETS</Badge>
                      <span className="text-xs text-muted-foreground">UK port operations</span>
                    </div>
                  )}
                </div>
              </div>
              <div>
                <div className="text-sm font-medium mb-2">Cross-Framework Insights:</div>
                <div className="space-y-1 text-xs text-muted-foreground">
                  <div>â€¢ Total annual fuel: {vesselData.fuelConsumption.toLocaleString()} tonnes</div>
                  <div>â€¢ Vessel size: {vesselData.grossTonnage.toLocaleString()} GT</div>
                  <div>â€¢ Operation type: {vesselData.voyageType === 'intra-eu' ? 'Intra-EU' : 'Extra-EU'} voyages</div>
                  <div>â€¢ Frameworks aligned: Data consistency verified</div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default IntegratedFrameworkManager;

client/src/components/Navigation.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Ship, BarChart3, Calculator, Database, Settings, Menu, X } from "lucide-react";

interface NavigationProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

const Navigation = ({ activeTab, onTabChange }: NavigationProps) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const navigationItems = [
    { id: "dashboard", label: "Dashboard", icon: BarChart3, badge: null },
    { id: "vessels", label: "Vessels", icon: Ship, badge: "12" },
    { id: "compliance", label: "Compliance", icon: Database, badge: null },
    { id: "calculator", label: "Calculate & Planning", icon: Calculator, badge: null },
    { id: "data-integration", label: "Data Integration", icon: Database, badge: null },
    { id: "settings", label: "Settings", icon: Settings, badge: null }
  ];

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    console.log('Mobile menu toggled:', !isMobileMenuOpen);
  };

  const handleTabClick = (tabId: string) => {
    onTabChange(tabId);
    setIsMobileMenuOpen(false);
    console.log('Tab changed to:', tabId);
  };

  return (
    <nav className="bg-card border-b border-border" data-testid="navigation">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
