  id,
  name,
  imoNumber,
  type,
  flag,
  grossTonnage,
  complianceStatus,
  ghgIntensity,
  targetIntensity,
  fuelConsumption,
  creditBalance,
  onViewDetails
}: VesselCardProps) => {
  const getStatusBadgeVariant = () => {
    switch (complianceStatus) {
      case "compliant":
